import React, { useState, useEffect } from "react";
import axios from "axios";
import { useParams } from "react-router";
import "./widget.css";

const Experience = (props) => {
  const param = useParams();
  const [title, SetTitle] = useState("");
  const [description, Setdescription] = useState("");
  const [likes, setlikes] = useState("");
  const [dislikes, setdislikes] = useState("");
  const [isLoading, SetisLoading] = useState(false);
  const [widgetclicked, Setwidgetclicked] = useState(0)
  useEffect(() => {
    SetisLoading(true);
    axios
      .get(
        `http://localhost:8080/Experience/getExperience?ExperienceID=${param.ExperienceID}`,
        {
          headers: {
            Authorization: `Bearer ${localStorage.getItem("jwt")}`,
          },
        }
      )
      .then((res) => {
        SetisLoading(false);
        SetTitle(res.data.title);
        Setdescription(res.data.description);
        setlikes(res.data.likes);
        setdislikes(res.data.dislikes);
        SetisLoading(res.data.SetisLoading);
      })
      .catch((error) => {
        SetisLoading(false);
        console.log(error.response);
      });
  }, [widgetclicked]);


  const Likehandler = e => {
      e.preventDefault()
      let Ids = {
        experienceID: param.ExperienceID,
        personID: localStorage.getItem('personID')
      }
      axios
      .post(`http://localhost:8080/Experience/like?ExperienceID=${param.ExperienceID}`, Ids, {
        headers: {
          "Content-Type": "application/json",
          Authorization: `Bearer ${localStorage.getItem("jwt")}`,
        },
      })
      //this will cause the likes and dislikes to change their value and thus the axios 
      //request on the very top cuases to run again and update with the number of likes obtain 
      //from the server
      Setwidgetclicked((prev)=>{
        return prev+1
    })
  }

  const DisLikehandler = e => {
    e.preventDefault()
    let Ids = {
      experienceID: param.ExperienceID,
      personID: localStorage.getItem('personID')
    }
    axios
    .post(`http://localhost:8080/Experience/dislike?ExperienceID=${param.ExperienceID}`, Ids, {
      headers: {
        "Content-Type": "application/json",
        Authorization: `Bearer ${localStorage.getItem("jwt")}`,
      },
    })
    Setwidgetclicked((prev)=>{
        return prev+1
    })
}

  return (
    <div>
      {isLoading && <p>obtaining data...</p>}
      {!isLoading && (
        <>
          <div class="sample-header">
            <div class="sample-header-section">
              <h1>{title}</h1>
            </div>
          </div>
          <div class="sample-section-wrap">
            <div class="sample-section" style={{ whiteSpace: "pre-wrap" }}>
              {description}
            </div>
          </div>
          <h2> Likes: {likes} Dislikes: {dislikes}</h2>
          <div className="widgetactive">
          <i onClick={Likehandler} class="fa fa-thumbs-up"/>
          </div>
          <div className="widget">
          <i onClick={DisLikehandler} class="fa fa-thumbs-down"/> 
          </div>
         
         
         
        </>
      )}
    </div>
  );
};

export default Experience;
