import React, { useState, useEffect } from "react";
import {
  NotificationContainer,
  NotificationManager,
} from "react-notifications";
import "react-notifications/lib/notifications.css";
let initFirst = true;

const LoginError = (props) => {
  let initialState = "Please fill in the form and click on send!";
  const [msg, setmsg] = useState(initialState);

  useEffect(() => {
    if (initFirst) {
      initFirst = false;
      return;
    }
    if (props.authResults) {
      NotificationManager.success(props.to, "Correct credientials");
      setmsg("Message sent successfully.");
    } else if (!props.authResults) {
      NotificationManager.error("Wrong credientials", 5000, () => {});
      setmsg("Wrong username or password!");
    }
  }, [props.authResults]);
  return <div>{msg}
  <NotificationContainer /></div>;
};

export default LoginError;
