import axios from "axios";
import React, { useState, useEffect } from "react";
import { Switch, Route,NavLink } from "react-router";
import StudentProfileInfoTable from "./StudentProfileInfoTable";
import UpdateStudentProfile from "./UpdateStudentProfile";

const StudentProfile = (props) => {
  const [user, setuser] = useState({});
  const [isLoading, SetisLoading] = useState(false);
  useEffect(() => {
    SetisLoading(true);
    axios
      .get(
        `http://localhost:8080/Student/getStudent?username=${localStorage.getItem(
          "username"
        )}`,
        {
          headers: {
            Authorization: `Bearer ${localStorage.getItem("jwt")}`,
          },
        }
      )
      .then((res) => {
        SetisLoading(false);
        setuser(res.data);
        console.log(res.data);
      })
      .catch((error) => {
        SetisLoading(false);
        console.log(error.response);
      });
  }, []);

  return (
    <div>
      Student profile
      <StudentProfileInfoTable user={user} />
      {isLoading && <p>Obtaining data...</p>}
      <Switch>
        <Route path="/Student/Profile/Update">
          <UpdateStudentProfile
            firstname={user.firstname}
            lastname={user.lastname}
            dateOfBirth={user.dateOfBirth}
            email={user.email}
          />
        </Route>
      </Switch>
      <li>
        <NavLink to="/Student/Profile/Update">
          Update personal information
        </NavLink>
      </li>
    </div>
  );
};

export default StudentProfile;
