import React, { useState, useEffect } from "react";
import axios from "axios";

const StudentContactInfo = (props) => {
    const [studentQuestion, setstudentQuestion] = useState('')
  //we want to have a text where whereby students can specify questions.
    useEffect(()=>{
        axios
        .post("http://localhost:8080/Experience/Add", newExperience, {
          headers: {
            "Content-Type": "application/json",
            Authorization: `Bearer ${localStorage.getItem("jwt")}`,
          },
        })
    }, [])
  //we would like to have a history of

  const onChangestudentQuestionHandler = e => {
      setstudentQuestion(e.target.value);
  }
  return (
    <div>
      <h1>Specify your question: </h1>
      <textarea placeholder="type here..."
      value={studentQuestion} onChange={onChangestudentQuestionHandler}/>
      <button>Send question</button>
    </div>
  );
};

export default StudentContactInfo;
