import React, {useState, useEffect} from "react";
import { useParams } from "react-router-dom";
import axios from 'axios';

const Experience = (props) => {
  const params = useParams();
  const [title, SetTitle] = useState("");
  const [description, Setdescription] = useState("");
  const [likes, setlikes] = useState("");
  const [dislikes, setdislikes] = useState("");
  const [isLoading, SetisLoading] = useState(false);
    console.log(params.ExperienceID)
  useEffect(() => {
    SetisLoading(true);
    axios
      .get(
        `http://localhost:8080/Experience/getExperience?ExperienceID=${params.ExperienceID}`,
        {
          headers: {
            Authorization: `Bearer ${localStorage.getItem("jwt")}`,
          },
        }
      )
      .then((res) => {
        SetisLoading(false);
        SetTitle(res.data.title);
        Setdescription(res.data.description);
        setlikes(res.data.likes);
        setdislikes(res.data.dislikes);
        SetisLoading(res.data.SetisLoading);
      })
      .catch((error) => {
        SetisLoading(false);
        console.log(error.response);
      });
  }, []);

  return (
    <div>
      {isLoading && <p>obtaining data...</p>}
      {!isLoading && (
        <>
        hi
          <div class="sample-header">
            <div class="sample-header-section">
              <h1>{title}</h1>
            </div>
          </div>
          <div class="sample-section-wrap">
            <div class="sample-section">{description}</div>
          </div>
          <h2> Likes: {likes}</h2>
          <h2> Dislikes: {dislikes}</h2>
        </>
      )}
    </div>
  );
};

export default Experience;
