import React, { useState } from "react";

function AdminSignUpStudent() {
  const initialState = "";
  const [firstName, setfirstName] = useState(initialState);
  const [lastName, setlastName] = useState(initialState);
  const [dateOfBirth, setdateOfBirth] = useState(initialState);
  const [email, setemail] = useState(initialState);
  const [specialization, setspecialization] = useState(initialState);
  const [username, setusername] = useState(initialState);
  const [password, setpassword] = useState(initialState);

  const onChangeFirstTime = (e) => {
    setfirstName(e.target.value);
  };

  const onChangeLastname = (e) => {
    setlastName(e.target.value);
  };

  const onChangeDateOfBirth = (e) => {
    setdateOfBirth(e.target.value);
  };

  const onChangeEmail = (e) => {
    setemail(e.target.value);
  };

  const onChangeSpecialization = (e) => {
    setspecialization(e.target.value);
  };

  const onChangeUsername = (e) => {
    setusername(e.target.value);
  };

  const onChangePassword = (e) => {
    setpassword(e.target.value);
  };

  const submitHandler = (e) => {
    e.preventDefault();

    let newStudent = {
      firstName: firstName,
      lastName: lastName,
      dateOfBirth: dateOfBirth,
      email: email,
      specialization: specialization,
      username: username,
      password: password,
      code:
        Math.random().toString(36).substring(2, 15) +
        Math.random().toString(36).substring(2, 15),
      role: "Student",
    };
    console.log(newStudent);
    fetch("http://localhost:8080/Student/add", {
      method: "POST",
      body: newStudent,
      headers: {
        Authorization: `Bearer ${localStorage.getItem("jwt")}`,
      },
    });
  };

  return (
    <form onSubmit={submitHandler}>
      <label>Add a student</label>
      <input
        type="text"
        placeholder="firstName"
        onChange={onChangeFirstTime}
        value={firstName}
      />
      <input
        type="text"
        placeholder="lastName"
        onChange={onChangeLastname}
        value={lastName}
      />
      <input
        type="date"
        placeholder="dateOfBirth"
        onChange={onChangeDateOfBirth}
        value={dateOfBirth}
      />
      <input
        type="email"
        placeholder="email"
        onChange={onChangeEmail}
        value={email}
      />
      <input
        type="text"
        placeholder="specialization"
        onChange={onChangeSpecialization}
        value={specialization}
      />
      <input
        type="text"
        placeholder="username"
        onChange={onChangeUsername}
        value={username}
      />
      <input
        type="text"
        placeholder="password"
        onChange={onChangePassword}
        value={password}
      />
      <button type="submit">Add</button>
    </form>
  );
}

export default AdminSignUpStudent;

