import React, { useState,useEffect } from "react";

let initFirst = true;

const LoginError = (props) => {
  let initialState = "Please fill in the form and click on send!";
  const [msg, setmsg] = useState(initialState);

  useEffect(() => {
    if (initFirst) {
      initFirst = false;
      return;
    }
    if (props.authResults) {
      NotificationManager.success(
        props.to,
        "Email has been sent successfully to"
      );
      setmsg("Message sent successfully.");
    } else if (!props.authResults) {
      NotificationManager.error(
        "An error occured!",
        "Click me!",
        5000,
        () => {}
      );
      setmsg("Error message has not been sent.");
    }
  }, [props.authResults]);
  return <div>{msg}</div>;
};

export default LoginError;
