import axios from "axios";
import React, { useState, useEffect } from "react";
import TeacherSearchingCard from "./TeacherSearchingCard";

const SPECIALIZATIONS = [
  "databases management", 
  "front end developer"
]
function StudentSearchingTeachers() {
  const [chosenSpecialization, setchosenSpecialization] = useState('');
  const [teachers, setTeachers] = useState([]);
  const [isLoading, SetisLoading] = useState(false);

  useEffect(() => {
    SetisLoading(true);
    axios
      .get(`http://localhost:8080/Teacher`, {
        headers: {
          Authorization: `Bearer ${localStorage.getItem("jwt")}`,
        },
      })
      .then((res) => {
        SetisLoading(false);
        setTeachers(() => {
          return res.data;
        });
        console.log(res.data);
      })
      .catch((error) => {
        SetisLoading(false);
        console.log(error.response);
      });
  }, []);

  return (
    <div>
      {isLoading && <p>Obtaining data...</p>}
      
      {!isLoading &&
        teachers.map((teacher) => {
          return (
            <TeacherSearchingCard key={teacher.personID} firstName={teacher.firstName} 
            lastName={teacher.lastName} email={teacher.email} specialization={teacher.specialization}/>
          );
        })
        }
    </div>
  );
}

export default StudentSearchingTeachers;

