import React, { useState, useEffect } from "react";
import axios from "axios";
import './StudentStatisticsCard.css';

import FusionCharts from "fusioncharts/core";
import Column2D from "fusioncharts/viz/column2d";

FusionCharts.addDep(Column2D);

const StudentStatisticsCard = (props) => {
  const [student, setStudent] = useState({});
  const [isLoading, SetisLoading] = useState(false);
  const [AmountOfAskedQuestions, SetAmountOfAskedQuestions] = useState(0);
  const [AmountOfSharedExperiences, setAmountOfSharedExperiences] = useState(0);


  useEffect(() => {
    SetisLoading(true);
    axios
      .get(`http://localhost:8080/Student/${props.studentID}`, {
        headers: {
          Authorization: `Bearer ${localStorage.getItem("jwt")}`,
        },
      })
      .then((res) => {
        SetisLoading(false);
        setStudent(res.data);
        axios
        .get(`http://localhost:8080/Admin/getAmountOfSharedExperienceByStudent?studentID=${student.personID}`, {
          headers: {
            Authorization: `Bearer ${localStorage.getItem("jwt")}`,
          },
        }).then(
            (sharedexperiencesRes)=> {
                setAmountOfSharedExperiences(sharedexperiencesRes.data)
            }
        )

        axios
        .get(`http://localhost:8080/Admin/getAmountOfAskedQuestionsByStudent?studentID=${student.personID}`, {
          headers: {
            Authorization: `Bearer ${localStorage.getItem("jwt")}`,
          },
        }).then(
            (askedQuestionsRes)=> {
                SetAmountOfAskedQuestions(askedQuestionsRes.data)
            }
        )
      })
      .catch((error) => {
        SetisLoading(false);
        console.log(error.response);
      });
  }, []);


  FusionCharts.ready(function () {
    // chart instance
    var chart = new FusionCharts({
        type: "column2d",
        renderAt: student.personID +"-chart-container" , // container where chart will render
        width: "600",
        height: "400",
        dataFormat: "json",
        dataSource: {
            // chart configuration
            chart: {
                caption: "",
                subcaption: ""
            },
            // chart data
            data: [
                { label: "Shared experiences", value:  AmountOfSharedExperiences},
                { label: "Questions asked", value:  AmountOfAskedQuestions},
            ]
        }
    }).render();
})

  return (
    <div>
      {isLoading && <p>Loading...</p>}

      {!isLoading && (
        <p>
          <div class="statisticsStudentCard">
            <div class="fullname">{student.firstName} {student.lastName}</div>
            <div id={student.personID +"-chart-container"} className="two-d-chart">
            </div>
          </div>
        </p>
      )}
    </div>
  );
};



export default StudentStatisticsCard;
