import React, { useState, useEffect } from "react";
import axios from "axios";
import ContactCardTeacher from "../../Utility/ContactCardTeacher";

const StudentContactHistory = (props) => {
  const [contacts, setcontacts] = useState([]);
  const [teachers, setteachers] = useState([]);
  const [isLoading, SetisLoading] = useState(false);
  useEffect(() => {
    SetisLoading(true);
    axios
      .get(
        `http://localhost:8080/ContactInfo/GetAllContacts?studentID=${localStorage.getItem(
          "personID"
        )}`,
        {
          headers: {
            Authorization: `Bearer ${localStorage.getItem("jwt")}`,
          },
        }
      )
      .then((res) => {
        SetisLoading(false);
        setcontacts(res.data);
        console.log(res.data);
      })
      .catch((error) => {
        SetisLoading(false);
        console.log(error.response);
      });
  }, []);

  return (
    <div>
      {isLoading && <p>Obtaining data...</p>}
      {!isLoading &&
        contacts.map((contact) => {

          console.log("teacher id " + contact.teacherID);
          axios.get(`http://localhost:8080/Teacher/${contact.teacherID}`, {
            headers: {
              Authorization: `Bearer ${localStorage.getItem("jwt")}`,
            },
          })
          .then((res) => {
              console.log(res.data)
              setteachers(res.data)
          });
          teachers.map((teacher)=> {
            return (
                <ContactCardTeacher
                  firstname={teachers.firstName}
                  lastname={teachers.lastName}
                  studentQuestion={teacher.studentQuestion}
                  teacherResponse={teacher.teacherResponse}
                  pending={teacher.pending}
                  wasUseful={teacher.wasUseful}
                  wasNotUseful={teacher.wasNotUseful}
                  teacherID={teacher.teacherID}
                  contactID={teacher.contactID}
                />
              );
          })
        })}
    </div>
  );
};

export default StudentContactHistory;
