import axios from "axios";
import React, { useState } from "react";
import LoginResults from "./LoginResults";
import { useDispatch, useSelector } from "react-redux";
import {AuthActions} from '../../Store/AuthSlice';

const Login = (props) => {
    const dispatch = useDispatch();


  const [isLoggedIn, SetisLoggedIn] = useState(false);
  const [isLogginClicked, SetisLogginClicked] = useState(false);
  const [authResults, SetauthResults] = useState(false);
  const [isLoading, SetisLoading] = useState(false);
  const [username, SetUsername] = useState("");
  const [password, SetPassword] = useState("");

  const OnUsernameChangeHandler = (e) => {
    SetUsername(e.target.value);
  };

  const OnPasswordChangeHandler = (e) => {
    SetPassword(e.target.value);
  };

  const SubmitHandler = (e) => {
    e.preventDefault();
    //assign username and password to redux
    console.log("username: " + username + ", password" + password);

    SetisLoading(true);
    if (isLoggedIn) {
    } else {
      axios
        .post("http://localhost:8080/authenticate", {
          username: username,
          password: password,
        })
        .then((repos) => {
          SetisLoading(false);
          SetauthResults(true);
          dispatch(AuthActions.login(repos.data));
          console.log(repos.data);
        })
        .catch((error) => {
          SetisLoading(false);
          SetisLogginClicked(true);
          SetauthResults(false);
        });
    }
  };
  return (
    <div>
      <form onSubmit={SubmitHandler}>
        <input
          type="text"
          placeholder="your username"
          value={username}
          onChange={OnUsernameChangeHandler}
        />
        <input
          type="password"
          placeholder="your password"
          value={password}
          onChange={OnPasswordChangeHandler}
        />
        <button type="submit"> login</button>

        {isLoading && <p>Sending request...</p>}
        {isLogginClicked && <LoginResults authResults={authResults} />}
      </form>
    </div>
  );
};

export default Login;
