import React from "react";
import { NavLink,  Link } from "react-router-dom";
import "./experiencecard.css";
import { useSelector } from "react-redux";
const ExperienceCard = (props) => {
  const isLoggedin = useSelector((state) => state.auth.isLoggedin);
  const userType = useSelector((state) => state.auth.userType);
  let StudentURL = "/Student/Experience/"+props.ExperienceID;
  let TeacherURL = "/Teacher/Experience/"+props.ExperienceID;
  return (
    <div>
      <div class="main-container">
        <div class="heading-container"></div>
        <div class="card-container">
          <div class="cardExp card-1">
            <div class="card__icon">
              <i class="fas fa-bolt"></i>
            </div>
            <p class="card__exit">
              <i class="fas fa-times"></i>
            </p>
            <h2 class="card__title">{props.title}</h2>
            <p class="card__apply">
              {isLoggedin && userType === "Student" && (
                <Link to={StudentURL}>
                  Check it out <i class="fas fa-arrow-right"></i>
                </Link>
              )}
              {isLoggedin && userType === "Teacher" && (
                <Link to={TeacherURL}>
                  Check it out <i class="fas fa-arrow-right"></i>
                </Link>
              )}
            </p>
          </div>
        </div>
      </div>
    </div>
  );
};

export default ExperienceCard;
