import axios from "axios";
import React, { useState, useEffect } from "react";

function StudentSearchingTeachers() {
  const [teachers, setTeachers] = useState([]);
  const [isLoading, SetisLoading] = useState(false);
  useEffect(() => {
    SetisLoading(true);
    axios
      .get(`http://localhost:8080/Teacher`, {
        headers: {
          Authorization: `Bearer ${localStorage.getItem("jwt")}`,
        },
      })
      .then((res) => {
        SetisLoading(false);
        setTeachers(() => {
          return res.data;
        });
        console.log(res.data);
      })
      .catch((error) => {
        SetisLoading(false);
        console.log(error.response);
      });
  }, []);

  return (
    <div>
      {isLoading && <p>Obtaining data...</p>}
      {!isLoading &&
        teachers.map((teacher) => {
          return (
            <>
              <h4>first name: {teacher.firstName}</h4>
              <h4>last name: {teacher.lastName}</h4>
              <h4>email name: {teacher.email}</h4>
              <h4>specialization : {teacher.specialization}</h4>
              <br></br>
            </>
          );
        })}
    </div>
  );
}

export default StudentSearchingTeachers;
