import { createSlice } from "@reduxjs/toolkit";

let loginState = false;

if (
  localStorage.getItem("jwt") !== null &&
  localStorage.getItem("jwt") !== null
) {
  loginState = true;
}
let ObtainedUserType;
if (localStorage.getItem("userType") !== null) {
  ObtainedUserType = localStorage.getItem("userType");
}
const initAuth = {
  isLoggedin: loginState,
  userType: ObtainedUserType,
  expirationDate: 0,
  remainingDuration: 0,
};

const calculateRemainingTime = (expirationTime) => {
  const currentTime = new Date().getTime();
  const adjExpirationTime = new Date(expirationTime).getTime();

  const theremainingDuration = adjExpirationTime - currentTime;

  return theremainingDuration;
};

const AuthSlice = createSlice({
  name: "Authentication",
  initialState: initAuth,
  reducers: {
    logout(state) {
      state.isLoggedin = false;
      state.userType = "";
      state.expirationDate = "";
      localStorage.clear();
    },

    login(state, action) {
      state.isLoggedin = true;
      state.userType = action.payload.userType;
      state.expirationDate = action.payload.expirationDate;
      state.remainingDuration = calculateRemainingTime(state.expirationDate);

      localStorage.setItem("jwt", action.payload.jwt);
      localStorage.setItem("username", action.payload.username);
      localStorage.setItem("userType", action.payload.userType);
      localStorage.setItem("expirationDate", action.payload.expirationDate);

      
    },
  },
});

export const AuthActions = AuthSlice.actions;
export default AuthSlice.reducer;
