import React, { useState, useEffect } from "react";
import { NavLink } from "react-router-dom";
import { useSelector } from "react-redux";
function NavigationBar() {
  const isLoggedin = useSelector((state) => state.auth.isLoggedin);
  const [userType, setUserType] = useState("");

  useEffect(() => {
    if (localStorage.getItem("userType") !== null) {
      setUserType(localStorage.getItem("userType"));
    }
  }, []);
  return (
    <nav>
      <ul>
        {!isLoggedin && (
          <li>
            <NavLink to="/Login">Login</NavLink>
          </li>
        )}
        {isLoggedin && (
          <li>
            <NavLink to="/Logout">Logout</NavLink>
          </li>
        )}

        {isLoggedin && userType === "Student" && (
          <li>
            <NavLink to="/Welcome/StudentWelcomer">Student welcomer</NavLink>
          </li>
        )}

        {isLoggedin && userType === "Teacher" && (
          <li>
            <NavLink to="/Welcome/TeacherWelcomer">Student welcomer</NavLink>
          </li>
        )}

        {isLoggedin && userType === "Admin" && (
          <li>
            <NavLink to="/Welcome/AdminWelcomer">Student welcomer</NavLink>
          </li>
        )}
      </ul>
    </nav>
  );
}

export default NavigationBar;
