import React, {useState} from 'react'

const DateHandler = props => {
    const [obtainedTime, setobtainedTime] = useState(props.obtainedTimestamp);
    return (
        <>
        {
            new Date(obtainedTime*1000).toLocaleDateString("en-US")
        }
        </>
    )
}

export default DateHandler
