import axios from "axios";
import React, { useState, useEffect } from "react";
import TeacherSearchingCard from "./TeacherSearchingCard";

const SPECIALIZATIONS = [
  "databases management", 
  "front end developer"
]
function StudentSearchingTeachers() {
  const [chosenSpecialization, setchosenSpecialization] = useState('');
  const [teachers, setTeachers] = useState([]);
  const [isLoading, SetisLoading] = useState(false);

  useEffect(() => {
    SetisLoading(true);
    axios
      .get(`http://localhost:8080/Teacher`, {
        headers: {
          Authorization: `Bearer ${localStorage.getItem("jwt")}`,
        },
      })
      .then((res) => {
        SetisLoading(false);
        setTeachers(() => {
          return res.data;
        });
        console.log(res.data);
      })
      .catch((error) => {
        SetisLoading(false);
        console.log(error.response);
      });
  }, []);

  const onChangeFieldHandler = e => {
    e.preventDefault();
    setchosenSpecialization(e.target.value)
  }
  return (
    <div>
      {isLoading && <p>Obtaining data...</p>}
      {!isLoading &&
      <div><button onClick={onChangeFieldHandler} value="databases management">databases management</button>
      <button onClick={onChangeFieldHandler} value="front end developer">front end developer</button></div>}
      {!isLoading &&
        teachers.filter(val=>{
          if(chosenSpecialization === ''){
            return val
          }
          else if (val.specialization === chosenSpecialization) {
            return val
          }
        }).map((teacher) => {
          return (
            <TeacherSearchingCard key={teacher.personID} firstName={teacher.firstName} 
            lastName={teacher.lastName} email={teacher.email} specialization={teacher.specialization}/>
          );
        })
        }
    </div>
  );
}

export default StudentSearchingTeachers;
