import React, {useEffect} from "react";
import { NavLink, Link } from "react-router-dom";
import "./experiencecard.css";
import Experience from "./Experience";
import { useSelector } from "react-redux";

let urlteacher;
let urlstudent;
const ExperienceCard = (props) => {
  const isLoggedin = useSelector((state) => state.auth.isLoggedin);
  const userType = useSelector((state) => state.auth.userType);
  urlstudent = `/Student/Experiences/${props.ExperienceID}`;
  urlteacher = `/Teacher/Experiences/${props.ExperienceID}`;

  useEffect(() => {
    SetisLoading(true);
    axios
      .get(
        `http://localhost:8080/Experience/getExperience?ExperienceID=${param.ExperienceID}`,
        {
          headers: {
            Authorization: `Bearer ${localStorage.getItem("jwt")}`,
          },
        }
      )
      .then((res) => {
        SetisLoading(false);
        SetTitle(res.data.title);
        Setdescription(res.data.description);
        setlikes(res.data.likes);
        setdislikes(res.data.dislikes);
        SetisLoading(res.data.SetisLoading);
      })
      .catch((error) => {
        SetisLoading(false);
        console.log(error.response);
      });
  }, []);


  return (
    <>
      <div>
        <div class="main-container">
          <div class="heading-container"> hi </div>
          <div class="cardExp-container">
            <div class="cardExp card-1">
              <div class="cardExp__icon">
              </div>
              <p class="cardExp__exit">
              </p>
              <h2 class="cardExp__title">{props.title}</h2>
              <p class="cardExp__apply">
                {isLoggedin && userType === "Student" && (
                  <NavLink to={urlstudent}>
                    Check it out 
                  </NavLink>
                )}
                {isLoggedin && userType === "Teacher" && (
                  <NavLink to={urlteacher}>
                    Check it out
                  </NavLink>
                )}
              </p>
            </div>
          </div>
        </div>
      </div>
    </>
  );
};

export default ExperienceCard;
