import React, { useState } from "react";
import Results from "../../Utility/Results";
import axios from "axios";

function AdminDeleteTeacher() {
  //todo
  initialState = 0;
  const [TeacherID, setTeacherID] = useState(initialState);
  const [RespondStatus, setRespondStatus] = useState(undefined);

  const onChangeTeacherId = (e) => {
    setTeacherID(e.target.value);
  };
  const onSubmitHandler = (e) => {
    e.preventDefault();
    axios
      .delete(`http://localhost:8080/Teacher?personID=${TeacherID}`, {
        headers: {
          "Content-Type": "application/json",
          Authorization: `Bearer ${localStorage.getItem("jwt")}`,
        },
      })
      .then((respon) => {
        setRespondStatus(true);
      })
      .catch((error) => {
        setRespondStatus(false);
      });
  };
  return (
    <div>
      <form onSubmit={onSubmitHandler}>
        <label>
          Specify Teacher ID to delete the teacher from Henaknowledge.
        </label>
        <input
          type="number"
          placeholder="Teacher ID ex.5"
          onChange={onChangeTeacherId}
          value={TeacherID}
        />
        <button type="submit"> delete teacher</button>
      </form>

      {RespondStatus && (
        <Results
          Results={RespondStatus}
          init="please specifiy an ID and click on delete"
          successMsg="Teacher has been removed from henaknowledge."
          errorMsg="An error occured"
        />
      )}
    </div>
  );
}

export default AdminDeleteTeacher;
