import axios from "axios";
import React, { useState,useEffect } from "react";
import LoginResults from "./LoginResults";
import { useDispatch, useSelector } from "react-redux";
import { AuthActions } from "../../Store/AuthSlice";
import { useHistory } from "react-router-dom";

const Login = (props) => {
  const History = useHistory();
  const dispatch = useDispatch();
  const loginState = useSelector((state) => state.auth.isLoggedin);
  const remainingDuration = useSelector((state) => state.auth.remainingDuration);
  const [isLogginClicked, SetisLogginClicked] = useState(false);
  const [authResults, SetauthResults] = useState(false);
  const [isLoading, SetisLoading] = useState(false);
  const [username, SetUsername] = useState("");
  const [password, SetPassword] = useState("");

  const OnUsernameChangeHandler = (e) => {
    SetUsername(e.target.value);
  };

  const OnPasswordChangeHandler = (e) => {
    SetPassword(e.target.value);
  };

  const SubmitHandler = (e) => {
    e.preventDefault();

    SetisLoading(true);

    if (loginState) {
    } else {
      axios
        .post("http://localhost:8080/authenticate", {
          username: username,
          password: password,
        })
        .then((repos) => {
          SetisLoading(false);
          SetauthResults(true);
          dispatch(AuthActions.login(repos.data));
          History.replace('/')
        })
        .catch((error) => {
          SetisLoading(false);
          SetisLogginClicked(true);
          SetauthResults(false);
        });
    }
  };


  return (
    <div>
      <form onSubmit={SubmitHandler}>
        <input
          type="text"
          placeholder="your username"
          value={username}
          onChange={OnUsernameChangeHandler}
        />
        <input
          type="password"
          placeholder="your password"
          value={password}
          onChange={OnPasswordChangeHandler}
        />
        <button type="submit"> login</button>

        {isLoading && <p>Sending request...</p>}
        {isLogginClicked && <LoginResults authResults={authResults} />}
      </form>
    </div>
  );
};

export default Login;
