import axios from "axios";
import React, { useState, useEffect, Fragment } from "react";
import DateHandler from "../../Utility/DateHandler";

const UpdateStudentProfile = (props) => {
  const [user, setuser] = useState({});
  const [isLoading, SetisLoading] = useState(false);
  useEffect(() => {
    SetisLoading(true);
    axios
      .get(
        `http://localhost:8080/Student/getStudent?username=${localStorage.getItem(
          "username"
        )}`,
        {
          headers: {
            Authorization: `Bearer ${localStorage.getItem("jwt")}`,
          },
        }
      )
      .then((res) => {
        SetisLoading(false);
        setuser(res.data);
        console.log(res.data);
      })
      .catch((error) => {
        SetisLoading(false);
        console.log(error.response);
      });
  }, []);

  return (
    <form>
      {isLoading && <p>Retreiving information... </p>}
      {!isLoading && (
        <Fragment>
          <label>Update your profile</label>
          <input type="text" placeholder={user.firstName} />
          <input type="text" placeholder={user.lastName} />
          <input type="date" placeholder={<DateHandler obtainedTimestamp={user.dateOfBirth} />} />
          <input type="text" placeholder={user.email} />
        </Fragment>
      )}
    </form>
  );
};

export default UpdateStudentProfile;
