import React, { useState, useEffect } from "react";
import axios from "axios";
import "./StudentStatisticsCard.css";
import Chart from "./chart/Chart";

const StudentStatisticsCard = (props) => {
  const [student, setStudent] = useState({});
  const [isLoading, SetisLoading] = useState(false);
  const [AmountOfAskedQuestions, SetAmountOfAskedQuestions] = useState(0);
  const [AmountOfSharedExperiences, setAmountOfSharedExperiences] = useState(0);
  const [chartDataPoints, setchartDataPoints] = useState([
    { label: "Amount Of Asked Questions", value: 0 },
    { label: "Amount Of Shared Experiences", value: 0 },
  ]);
  useEffect(() => {
    SetisLoading(true);
    axios
      .get(`http://localhost:8080/Student/${props.studentID}`, {
        headers: {
          Authorization: `Bearer ${localStorage.getItem("jwt")}`,
        },
      })
      .then((res) => {
        SetisLoading(false);
        setStudent(res.data);
        axios
          .get(
            `http://localhost:8080/Admin/getAmountOfSharedExperienceByStudent?studentID=${student.personID}`,
            {
              headers: {
                Authorization: `Bearer ${localStorage.getItem("jwt")}`,
              },
            }
          )
          .then((sharedexperiencesRes) => {
            setAmountOfSharedExperiences(sharedexperiencesRes.data);

            axios
              .get(
                `http://localhost:8080/Admin/getAmountOfAskedQuestionsByStudent?studentID=${student.personID}`,
                {
                  headers: {
                    Authorization: `Bearer ${localStorage.getItem("jwt")}`,
                  },
                }
              )
              .then((askedQuestionsRes) => {
                SetAmountOfAskedQuestions(askedQuestionsRes.data);
                setchartDataPoints([
                  {
                    label: "Amount Of Asked Questions",
                    value: AmountOfAskedQuestions,
                  },
                  {
                    label: "Amount Of Shared Experiences",
                    value: AmountOfSharedExperiences,
                  },
                ]);
              });
          })
          .catch((error) => {
            SetisLoading(false);
            console.log(error.response);
          });
      });
  }, []);

  return (
    <div>
      {isLoading && <p>Loading...</p>}

      {!isLoading && (
        <p>
          <div class="statisticsStudentCard">
            <div class="fullname">
              {student.firstName} {student.lastName}
            </div>
            <div className="two-d-chart"></div>
          </div>
          
        </p>
      )}
      <Chart dataPoints={chartDataPoints} />
    </div>
  );
};
export default StudentStatisticsCard;
