import React from 'react'

const DateHandler = props => {
    let unixTime  = props.obtainedTimestamp;
    const transformedDate = new Date(unixTime*1000);
    const date = transformedDate.toLocaleDateString("en-US")

    return (
        {
            date
        }
    )
}

export default DateHandler
