import React, { useState, useEffect } from "react";
import axios from "axios";
import ContactCardTeacher from "../../Utility/ContactCardTeacher";

const StudentContactHistory = (props) => {
  const [teachers, setteachers] = useState([]);
  const [isLoading, SetisLoading] = useState(false);
  const [firstname, setFirstName] = useState('');
  const [lastname, setlastname] = useState('');
  useEffect(() => {
    SetisLoading(true);
    axios
      .get(
        `http://localhost:8080/ContactInfo/GetAllContacts?studentID=${localStorage.getItem(
          "personID"
        )}`,
        {
          headers: {
            Authorization: `Bearer ${localStorage.getItem("jwt")}`,
          },
        }
      )
      .then((res) => {
        SetisLoading(false);
        setteachers(res.data);
        console.log(res.data);
      })
      .catch((error) => {
        SetisLoading(false);
        console.log(error.response);
      });
  }, []);

  return (
    <div>
      {isLoading && <p>Obtaining data...</p>}
      {!isLoading &&
        teachers.map((teacher) => {

          console.log("teacher id " + teacher.teacherID);
          axios.get(`http://localhost:8080/Teacher/${teacher.teacherID}`, {
            headers: {
              Authorization: `Bearer ${localStorage.getItem("jwt")}`,
            },
          })
          .then((res) => {
              console.log(res.data)
              setFirstName(res.data.firstName)
              setlastname(res.data.lastName)
          });
          return (
            <ContactCardTeacher
              firstname={firstname}
              lastname={lastname}
              studentQuestion={teacher.studentQuestion}
              teacherResponse={teacher.teacherResponse}
              pending={teacher.pending}
              wasUseful={teacher.wasUseful}
              wasNotUseful={teacher.wasNotUseful}
              teacherID={teacher.teacherID}
              contactID={teacher.contactID}
            />
          );
        })}
    </div>
  );
};

export default StudentContactHistory;
