import axios from "axios";
import React, { useState, useEffect, Fragment } from "react";
import Results from "../../Utility/Results";
import Logo from "../../../Logo/Logo";
import { useDispatch, useSelector } from "react-redux";
import { UPIactions } from "../../../Store/UpdatePersonalinfoSlice";


const UpdateStudentProfile = (props) => {
  const dispatch = useDispatch();


  const [user, setuser] = useState({});
  const [isLoading, SetisLoading] = useState(false);
  useEffect(() => {
    SetisLoading(true);
    axios
      .get(
        `http://localhost:8080/Student/getStudent?username=${localStorage.getItem(
          "username"
        )}`,
        {
          headers: {
            Authorization: `Bearer ${localStorage.getItem("jwt")}`,
          },
        }
      )
      .then((res) => {
        SetisLoading(false);
        setuser(res.data);
        console.log("data:");
        console.log(res.data);
        dispatch(UPIactions.updateInfo(res.data))
      })
      .catch((error) => {
        SetisLoading(false);
        console.log(error.response);
      });
  }, []);

  const [firstName, setfirstName] = useState("");
  const [lastName, setlastName] = useState("");
  const [email, setemail] = useState("");

  const onChangeFirstnameHandler = (e) => {
    setfirstName(e.target.value);
  };

  const onChangeLastnameHandler = (e) => {
    setlastName(e.target.value);
  };

  const onChangeEmailHandler = (e) => {
    setemail(e.target.value);
  };

  const [updateStatus, SetUpdateStatus] = useState(undefined);

  const onSubmitHandler = (e) => {
    e.preventDefault();

    fetch(
      `http://localhost:8080/Student?PersonID=${user.personID}&first_name=${firstName}&last_name=${lastName}&email=${email}`,
      {
        method: "PUT",

        headers: {
          Authorization: `Bearer ${localStorage.getItem("jwt")}`,
          "Access-Control-Allow-Origin": "*",
          "Content-Length": "0",
        },
      }
    )
      .then((respo) => {
        SetUpdateStatus(true);
      })
      .catch((error) => {
        SetUpdateStatus(false);
        console.log(error);
      });
  };

  return (
    <div class="session">
      <div class="left">
        {
          console.log("fn" +  user.firstName)
        }
        <Logo />
      </div>
      <div className="right">
        <form onSubmit={onSubmitHandler}>
          <h1 className="movingtext">
            <span>Henaknowledge platform</span> 
          </h1>
          <p>
            You can update your personal information in this section.
          </p>
          {isLoading && <p>Retreiving information... </p>}
          {!isLoading && (
            <Fragment>
              <label>Update your profile</label>
              <input
                type="text"
                required=""
                onChange={onChangeFirstnameHandler}
                value={user.firstName}
              />
              <input
                type="text"
                required
                onChange={onChangeLastnameHandler}
                value={user.lastName}
              />
              <input
                type="email"
                required
                onChange={onChangeEmailHandler}
                value={user.email}
              />

              <button type="submit" className="lf--submit">Update information</button>
            </Fragment>
          )}
          {updateStatus && (
            <Results
              Results={updateStatus}
              successMsg="Information updated successfully"
              errorMsg="Something went wrong."
              init="Please fill in the form and click on update"
            />
          )}
        </form>
      </div>
    </div>
  );
};

export default UpdateStudentProfile;
