import React from "react";
import "./infocard.css";
import TeacherIcon from "../../../Assets/teacherIcon";

import Popup from "reactjs-popup";
import "reactjs-popup/dist/index.css";

const TeacherSearchingCard = (props) => {
  return (
    <div class="card">
      <div class="img-avatar">
        <TeacherIcon />
      </div>
      <div class="card-text">
        <div class="portada"></div>
        <div class="title-total">
          <div class="title">Teacher</div>
          <h2>
            {props.firstName} {props.lastName}
          </h2>

          <div class="desc">
            <div>
              <h4>email name: {props.email}</h4>
              <h4>specialization : {props.specialization}</h4>
              <br></br>
            </div>
          </div>
          <div class="actions">
            {props.deleteStatus && (
              <button onClick={props.deleteFun}>Fire</button>
            )}
            {props.contact && (
              <Popup trigger={<button>contact</button>} modal >
                <h1 >{props.email}</h1>
                <p> * The feature of contacting the teacher is still in developing!</p>
              </Popup>
            )}
          </div>
        </div>
      </div>
    </div>
  );
};

export default TeacherSearchingCard;
