import React, { useState, useEffect } from "react";
import axios from "axios";
import { useParams } from "react-router";
import "./widget.css";

let widgetLikeStyle;
let widgetDisLikeStyle;

const Experience = (props) => {
  const param = useParams();
  const [title, SetTitle] = useState("");
  const [description, Setdescription] = useState("");
  const [likes, setlikes] = useState("");
  const [dislikes, setdislikes] = useState("");
  const [isLoading, SetisLoading] = useState(false);
  const [ExperienceOpinion, SetExperienceOpinion] = useState({});

  useEffect(() => {
    SetisLoading(true);
    axios
      .get(
        `http://localhost:8080/Experience/getExperience?ExperienceID=${param.ExperienceID}`,
        {
          headers: {
            Authorization: `Bearer ${localStorage.getItem("jwt")}`,
          },
        }
      )
      .then((res) => {
        SetisLoading(false);
        SetTitle(res.data.title);
        Setdescription(res.data.description);
        setlikes(res.data.likes);
        setdislikes(res.data.dislikes);
        SetisLoading(res.data.SetisLoading);
      })
      .catch((error) => {
        SetisLoading(false);
        console.log(error.response);
      });

    axios
      .get(
        `http://localhost:8080/Experience/getExperienceOpinion?PersonID=${localStorage.getItem(
          "personID"
        )}&ExperienceID=${param.ExperienceID}`,
        {
          headers: {
            Authorization: `Bearer ${localStorage.getItem("jwt")}`,
          },
        }
      )
      .then((res) => {
        console.log(res.data);
        SetExperienceOpinion(res.data);
      });
  }, [likes, dislikes]);

  const Likehandler = (e) => {
    let Ids = {
      experienceID: param.ExperienceID,
      personID: localStorage.getItem("personID"),
    };
    axios.post(
      `http://localhost:8080/Experience/like?ExperienceID=${param.ExperienceID}`,
      Ids,
      {
        headers: {
          "Content-Type": "application/json",
          Authorization: `Bearer ${localStorage.getItem("jwt")}`,
        },
      }
    );
    //this will cause the likes and dislikes to change their value and thus the axios
    //request on the very top cuases to run again and update with the number of likes obtain
    //from the server
    window.location.reload(true);
  };

  const DisLikehandler = (e) => {
    let Ids = {
      experienceID: param.ExperienceID,
      personID: localStorage.getItem("personID"),
    };
    axios.post(
      `http://localhost:8080/Experience/dislike?ExperienceID=${param.ExperienceID}`,
      Ids,
      {
        headers: {
          "Content-Type": "application/json",
          Authorization: `Bearer ${localStorage.getItem("jwt")}`,
        },
      }
    );
    window.location.reload(true);
  };

  if (ExperienceOpinion.likes === 0 && ExperienceOpinion.dislikes === 1) {
    widgetLikeStyle = "widget";
  } else if (
    ExperienceOpinion.likes === 0 &&
    ExperienceOpinion.dislikes === 0
  ) {
    widgetLikeStyle = "widget";
  } else if (
    ExperienceOpinion.likes === 1 &&
    ExperienceOpinion.dislikes === 0 &&
    ExperienceOpinion.ExperienceID === param.ExperienceID
  ) {
    widgetLikeStyle = "widgetactive";
  }
  if (ExperienceOpinion.likes === 1 && ExperienceOpinion.dislikes === 0) {
    widgetDisLikeStyle = "widget";
  } else if (
    ExperienceOpinion.likes === 0 &&
    ExperienceOpinion.dislikes === 0
  ) {
    widgetDisLikeStyle = "widget";
  } else if (
    ExperienceOpinion.likes === 0 &&
    ExperienceOpinion.dislikes === 1 &&
    ExperienceOpinion.ExperienceID === param.ExperienceID
  ) {
    widgetDisLikeStyle = "widgetactive";
  }

  return (
    <div>
      {isLoading && <p>obtaining data...</p>}
      {!isLoading && (
        <>
          <div class="sample-header">
            <div class="sample-header-section">
              <h1>{title}</h1>
            </div>

            <div className={widgetLikeStyle}>
              <i onClick={Likehandler} class="fa fa-thumbs-up" />
              Likes: {likes}
            </div>
            <div className={widgetDisLikeStyle}>
              <i onClick={DisLikehandler} class="fa fa-thumbs-down" />
              Dislikes: {dislikes}
            </div>
          </div>
          <div class="sample-section-wrap">
            <div class="sample-section" style={{ whiteSpace: "pre-wrap" }}>
              {description}
            </div>
          </div>
        </>
      )}
    </div>
  );
};

export default Experience;
