import axios from "axios";
import React, { useState, useEffect, Fragment } from "react";

const UpdateStudentProfile = (props) => {
  const [user, setuser] = useState({});
  const [isLoading, SetisLoading] = useState(false);
  useEffect(() => {
    SetisLoading(true);
    axios
      .get(
        `http://localhost:8080/Student/getStudent?username=${localStorage.getItem(
          "username"
        )}`,
        {
          headers: {
            Authorization: `Bearer ${localStorage.getItem("jwt")}`,
          },
        }
      )
      .then((res) => {
        SetisLoading(false);
        setuser(res.data);
        console.log(res.data);
      })
      .catch((error) => {
        SetisLoading(false);
        console.log(error.response);
      });
  }, []);

  const [firstName, setfirstName] = useState("");
  const [lastName, setlastName] = useState("");
  const [email, setemail] = useState("");

  const onChangeFirstnameHandler = (e) => {
    setfirstName(e.target.value);
  };

  const onChangeLastnameHandler = (e) => {
    setlastName(e.target.value);
  };

  const onChangeEmailHandler = (e) => {
    setemail(e.target.value);
  };

  const onSubmitHandler = (e) => {
    e.preventDefault();

      axios
      .put(
        `http://localhost:8080/Student?PersonID=${user.personID}&first_name=${firstName}&last_name=${lastName}&email=${email}`,
        {
          headers: {
            Authorization: `Bearer ${localStorage.getItem("jwt")}`,
            'Access-Control-Allow-Origin': '*',
          },
        }
      ).then(respo=>{
        console.log(respo)
      })
      .catch(error=>{
        console.log(error);
      })
  };

  return (
    <form onSubmit={onSubmitHandler}>
      {isLoading && <p>Retreiving information... </p>}
      {!isLoading && (
        <Fragment>
          <label>Update your profile</label>
          <input
            type="text"
            placeholder={user.firstName}
            onChange={onChangeFirstnameHandler}
            value={firstName}
          />
          <input
            type="text"
            placeholder={user.lastName}
            onChange={onChangeLastnameHandler}
            value={lastName}
          />
          <input
            type="email"
            placeholder={user.email}
            onChange={onChangeEmailHandler}
            value={email}
          />

          <button type="submit">Update information</button>
        </Fragment>
      )}
    </form>
  );
};

export default UpdateStudentProfile;
